flights_to_import = []
flight_id_mapping = {}  # Map original ID to array index

flights_data.each_with_index do |flight_data, index|
  original_id = flight_data.delete(:id)
  offset_days = flight_data.delete(:flight_date_offset_days)
  departure_offset = flight_data.delete(:departure_time_offset_seconds)
  arrival_offset = flight_data.delete(:arrival_time_offset_seconds)
  
  # 计算实际日期和时间
  flight_date = base_date + offset_days.days
  departure_time = base_datetime + departure_offset.seconds
  arrival_time = base_datetime + arrival_offset.seconds
  
  flight = Flight.new(
    **flight_data,
    flight_date: flight_date,
    departure_time: departure_time,
    arrival_time: arrival_time
  )
  
  flights_to_import << flight
  flight_id_mapping[original_id] = index
end

