offers_to_import = []

offers_data.each do |offer_data|
  original_id = offer_data.delete(:id)
  original_flight_id = offer_data.delete(:flight_id)
  flight_index = flight_id_mapping[original_flight_id]
  actual_flight = flights_to_import[flight_index]
  
  offer = FlightOffer.new(
    **offer_data,
    flight_id: actual_flight.id
  )
  
  offers_to_import << offer
end

